<?php/* * The Header for our theme. * * Displays all of the <head> section and everything up till <main id="main"> * * @package cwp */?><!DOCTYPE html><html <?php language_attributes(); ?>><head>	<title><?php wp_title( '|', true, 'right' ); ?></title>	<meta charset="<?php bloginfo( 'charset' ); ?>" />	<meta name="viewport" content="width=device-width, user-scalable=no" />	<link rel="profile" href="http://gmpg.org/xfn/11" />	<link rel="pingback" href="<?php bloginfo( 'pingback_url' ); ?>" />	<?php wp_head(); ?></head><body <?php body_class(); ?>>				<!--Header Start-->		<header>			<div class="header_center">				<?php 					if ( get_theme_mod( 'codeinwp_logo' ) ) :						echo '<div id="logo" style="background: url('							. get_theme_mod( 'codeinwp_logo' )							. ') no-repeat left center;">';							echo '<a href="' . esc_url( home_url( '/' ) ) . '"></a>';						echo '</div>';					else :							echo '<div id="no-logo">';						echo '<h1 class="site-title"><a href="'							. esc_url( home_url( '/' ) ).'" title="'							. esc_attr( get_bloginfo( 'name', 'display' ) )							. '" rel="home">'.get_bloginfo( 'name' ).'</a></h1>';						echo '<h2 class="site-description"><a href="'							. esc_url( home_url( '/' ) ).'" title="'							. esc_attr( get_bloginfo( 'name', 'display' ) )							. '" rel="home">'.get_bloginfo( 'description' ).'</a></h2>';						echo '</div>';					endif;				?>				<div class="menu_toggle"></div>				<?php 					if ( get_theme_mod( 'codeinwp_headerbutton_text') ) :						$header_button_text = get_theme_mod('codeinwp_headerbutton_text');					endif;										if ( get_theme_mod( 'codeinwp_headerbutton_link' ) ):						$header_button_link = get_theme_mod( 'codeinwp_headerbutton_link' );					endif;					if ( get_theme_mod( 'codeinwp_headerbutton') == 'show' ) {						if ( !empty( $header_button_text) && !empty( $header_button_link ) ) {							?>								<div class="buyalbum">									<a href="<?php echo $header_button_link; ?>">										<?php echo $header_button_text; ?></a>								</div>							<?php						}					}				?>				<nav id="header_nav" class="main-navigation">					<?php wp_nav_menu( array( 'theme_location' => 'primary', 'container' => '' ) ); ?>				</nav><!--/header_nav-->								<?php if ( get_header_image() != '' ) : ?>					<img src="<?php header_image(); ?>" alt="<?php bloginfo( 'name' ); ?>" >				<?php endif; ?>			</div><!--/header_center-->					</header>				<!--Header End-->