<?php/* * cwp functions and definitions * * @package music-band-lite * * Set the content width based on the theme's design and stylesheet. */ if ( ! isset( $content_width ) )	$content_width = 640; /* pixels */	function music_band_lite_setup() {	load_theme_textdomain( 'music-band-lite', get_template_directory() . '/languages' );	/*	 * Add default posts and comments RSS feed links to head	 */	add_theme_support( 'automatic-feed-links' );	/*	 * Enable support for Post Thumbnails on posts and pages	 */	add_theme_support( 'post-thumbnails' );		register_nav_menus( array(		'primary' => __( 'Primary Menu', 'music-band-lite' ),		'footer_menu' => __( 'Footer Menu', 'music-band-lite' )		) 	);	/*	 * Setup the WordPress core custom background feature.	 */	add_theme_support( 'custom-background', apply_filters( 'cwp_custom_background_args', array(		'default-color' => '#f5f4f9',		'default-image' => '',	) ) );		add_theme_support( 'post-formats', array( 'aside', 'gallery', 'image', 'audio' ) );	}add_action( 'after_setup_theme', 'music_band_lite_setup' );/* * Register widgetized area and update sidebar with default widgets */function music_band_lite_widgets_init() {	register_sidebar( array(		'name'          => __( 'Sidebar', 'music-band-lite' ),		'id'            => 'sidebar-1',		'before_widget' => '<div id="%1$s" class="widget %2$s">',		'after_widget'  => '</div>',		'before_title'  => '<h1 class="widget-title">',		'after_title'   => '</h1>',	) );}add_action( 'widgets_init', 'music_band_lite_widgets_init' );/* * Enqueue scripts and styles */function music_band_lite_scripts() {	wp_enqueue_style( 'music_band_lite-style', get_stylesheet_uri() );		wp_enqueue_script( 'jquery' );	wp_enqueue_script( 'music_band_lite-navigation', get_template_directory_uri() . '/js/navigation.js', array(), '20120206', true );		wp_enqueue_script( 'music_band_lite-tabs', get_template_directory_uri() . '/js/tabs.js', array( 'jquery' ), '20120206', true );		wp_enqueue_script( 'music_band_lite-slider', get_template_directory_uri() . '/js/jquery.slides.js', array( 'jquery' ), '20120206', true );		wp_enqueue_script( 'music_band_lite-customscript', get_template_directory_uri() . '/js/customscript.js', array( 'jquery', 'music_band_lite-tabs', 'music_band_lite-slider' ), '20120206', true );		wp_enqueue_script( 'music_band_lite-skip-link-focus-fix', get_template_directory_uri() . '/js/skip-link-focus-fix.js', array(), '20130115', true );		if ( is_singular() ) {		wp_enqueue_script( 'comment-reply' );	}		if ( is_singular() && wp_attachment_is_image() ) {		wp_enqueue_script( 'music_band_lite-keyboard-image-navigation', 			get_template_directory_uri() . '/js/keyboard-image-navigation.js', array( 'jquery' ), '20120202' );	}}add_action( 'wp_enqueue_scripts', 'music_band_lite_scripts' );/* * Implement the Custom Header feature. */$args = array(		/*'width'         => 1900,		'height'        => 125,		'default-image' => '',		'uploads'       => true,		'flex-width'    => true,		'flex-height'    => true,*/					      'flex-height' => true,           'height' => 125,          'flex-width' => true,           'width' => 1900,            'wp-head-callback' => $wphead_cb,         'admin-head-callback' => $adminhead_cb,            'admin-preview-callback' => $adminpreview_cb	  	  	);add_theme_support( 'custom-header', $args );/* * Custom template tags for this theme. */require get_template_directory() . '/inc/template-tags.php';/* * Custom functions that act independently of the theme templates. */require get_template_directory() . '/inc/extras.php';/* * Customizer additions. */require get_template_directory() . '/inc/customizer.php';/* * Load Jetpack compatibility file. */require get_template_directory() . '/inc/jetpack.php';/* editor style */function music_band_lite_add_editor_styles() {    add_editor_style( '/css/custom-editor-style.css' );}add_action( 'init', 'music_band_lite_add_editor_styles' );/* Default title */add_filter( 'the_title', 'music_band_lite_default_title' );function music_band_lite_default_title( $title ) {	if ( $title == '' )		$title = "Default title";	return $title;}