<?php/* * The Header for our theme. * * Displays all of the <head> section and everything up till <main id="main"> * * @package music-band-lite */?><!DOCTYPE html><html <?php language_attributes(); ?>><head>	<title><?php wp_title( '|', true, 'right' ); ?></title>	<meta charset="<?php bloginfo( 'charset' ); ?>" />	<meta name="viewport" content="width=device-width, user-scalable=no" />	<link rel="profile" href="http://gmpg.org/xfn/11" />	<link rel="pingback" href="<?php bloginfo( 'pingback_url' ); ?>" />	<?php wp_head(); ?></head><body <?php body_class(); ?>>				<!--Header Start-->		<header>			<div class="header_center">				<?php 					if ( get_theme_mod( 'codeinwp_logo' ) ) :						echo '<div id="logo" style="background: url('							. get_theme_mod( 'codeinwp_logo' )							. ') no-repeat left center;">';							echo '<a href="' . esc_url( home_url( '/' ) ) . '"></a>';						echo '</div>';					else :							echo '<div id="no-logo">';						echo '<h1 class="site-title"><a href="'							. esc_url( home_url( '/' ) ).'" title="'							. esc_attr( get_bloginfo( 'name', 'display' ) )							. '" rel="home">'.get_bloginfo( 'name' ).'</a></h1>';						echo '<h2 class="site-description"><a href="'							. esc_url( home_url( '/' ) ).'" title="'							. esc_attr( get_bloginfo( 'name', 'display' ) )							. '" rel="home">'.get_bloginfo( 'description' ).'</a></h2>';						echo '</div>';					endif;				?>				<div class="menu_toggle"></div>								<nav id="header_nav" class="main-navigation">					<?php wp_nav_menu( array( 'theme_location' => 'primary', 'container' => '' ) ); ?>				</nav><!--/header_nav-->							</div><!--/header_center-->					</header>		<?php 			if (!is_front_page()):				if ( get_theme_mod( 'top_banner_title' ) ) :					$top_banner_title = get_theme_mod( 'top_banner_title' );				endif;					if ( get_theme_mod( 'top_banner_text' ) ) :					$top_banner_text = get_theme_mod( 'top_banner_text' );				endif;					if ( get_header_image() != '' ) :					?>					<section id="subheader" class="subheader_news" style="background-image: url(<?php header_image(); ?>);">						<?php 							if ( isset( $top_banner_title) && $top_banner_title != '' )								echo '<div class="album_title">'.$top_banner_title.'</div>';							if ( isset( $top_banner_text) && $top_banner_text != '' )									echo '<p>'.$top_banner_text.'</p>';						?>					</section><!--/subheader-->					<?php					else:						if ( !empty( $top_banner_title ) || !empty( $top_banner_text )) {					?>							<section id="subheader" class="subheader_news" style="background-image: url('<?php echo get_template_directory_uri(); ?>/images/abovefooterbg.png');">								<?php 									if ( !empty( $top_banner_title ) ) {										echo '<div class="album_title">' . $top_banner_title . '</div>';									}									if ( !empty( $top_banner_text ) ) {										echo '<p>' . $top_banner_text . '</p>';									}								?>							</section><!--/subheader-->					<?php						}					endif;				endif; ?>		<div class="headerborder"></div>						<!--Header End-->