<?php/** * music-band-lite Theme Customizer * * @package music-band-lite *//** * Add postMessage support for site title and description for the Theme Customizer. * * @param WP_Customize_Manager $wp_customize Theme Customizer object. */function codeinwp_customize_register( $wp_customize ) {	class music_band_lite_Theme_Support extends WP_Customize_Control	{		public function render_content()		{		}	} 	$wp_customize->get_setting( 'blogname' )->transport         = 'postMessage';	$wp_customize->get_setting( 'blogdescription' )->transport  = 'postMessage';	$wp_customize->get_setting( 'header_textcolor' )->transport = 'postMessage';		$wp_customize->remove_control( 'header_textcolor' );			/**********************************/    /*******	THEME NOTES  **********/	/*********************************/		$wp_customize->add_section( 'codeinwp_theme_notes' , array(		'title'      => __('ThemeIsle theme notes','music-band-lite'),		'description' => sprintf( __( "Thank you for being part of this! We've spent almost 6 months building ThemeIsle without really knowing if anyone will ever use a theme or not, so we're very grateful that you've decided to work with us. Wanna <a href='http://themeisle.com/contact/' target='_blank'>say hi</a>?		<br/><br/><a href='http://themeisle.com/demo/?theme=Music Band Free' target='_blank' />View Theme Demo</a> | <a href='http://themeisle.com/forums/forum/music-band-free' target='_blank'>Get theme support</a><br/><br/><a href='http://themeisle.com/documentation-music-band-free' target='_blank'>Documentation</a>")),		'priority'   => 30,	));	$wp_customize->add_setting(        'codeinwp_theme_notes'	);		$wp_customize->add_control( new music_band_lite_Theme_Support( $wp_customize, 'codeinwp_theme_notes',	    array(	        'section' => 'codeinwp_theme_notes',	   )	));			/**********************************/	/**********  LOGO  ****************/	/**********************************/		$wp_customize->add_section( 'codeinwp_logo_section' , array(    	'title'       => __( 'Logo', 'music-band-lite' ),    	'priority'    => 31,    	'description' => __('Upload a logo to replace the default site name and description in the header','music-band-lite'),	) );	$wp_customize->add_setting( 'codeinwp_logo' );	$wp_customize->add_control( new WP_Customize_Image_Control( $wp_customize, 'themeslug_logo', array(	    'label'    => __( 'Logo', 'music-band-lite' ),	    'section'  => 'codeinwp_logo_section',	    'settings' => 'codeinwp_logo',	) ) );					/**********************************/    /***** 	TOP BANNER SECTION ********/	/**********************************/				/* title */	$wp_customize->add_setting( 'top_banner_title', 		array(			'default' => '', 'sanitize_callback' => 'music_band_lite_text_sanitization'		) 	);	$wp_customize->add_control('top_banner_title', array(		'label'   => 'Enter a title for the top banner',		'section' => 'header_image',		'settings'   => 'top_banner_title',		'priority' => 10	) );		/* text */	$wp_customize->add_setting( 'top_banner_text', 		array(			'default' => '', 'sanitize_callback' => 'music_band_lite_text_sanitization'		) 	);	$wp_customize->add_control('top_banner_text', array(		'label'   => 'Enter a text for the top banner',		'section' => 'header_image',		'settings'   => 'top_banner_text',		'priority' => 11	) );			/* END - TOP BANNER SECTION */				/**********************************************/	/******** SINGLE PAGE featured image *********/	/*********************************************/			$wp_customize->add_section( 'codeinwp_singlefeaturedimage_section', array(		'title'          => __( 'Single page featured image','music-band-lite' ),		'priority'       =>37,	) );		$wp_customize->add_setting('fi_single', array(        'default'        => 'show'    ));     $wp_customize->add_control('fi_single', array(        'label'      => __('Show or hide featured image on single page', 'music-band-lite'),        'section'    => 'codeinwp_singlefeaturedimage_section',        'settings'   => 'fi_single',        'type'       => 'radio',        'choices'    => array(            'show' => 'Show',            'hide' => 'Hide'        ),    ));	/* END - SINGLE PAGE featured image */			/****************************************************/	/*******  FRONT PAGE SLIDER *************************/	/****************************************************/			$wp_customize->add_section( 'codeinwp_slider_section', array(		'title'          => __( 'Slider section on first page','music-band-lite' ),		'priority'       => 41,	) );	/* show or hide slider */	$wp_customize->add_setting('slider_index', array(        'default'        => 'show'    ));     $wp_customize->add_control('slider_index', array(        'label'      => __('Show or hide slider on front page', 'music-band-lite'),        'section'    => 'codeinwp_slider_section',        'settings'   => 'slider_index',        'type'       => 'radio',        'choices'    => array(            'show' => 'Show',            'hide' => 'Hide'        ),    ));		/* first slide image */	$wp_customize->add_setting( 'slider_image1' );	$wp_customize->add_control( new WP_Customize_Image_Control( $wp_customize, 'slider_image1', array(	    'label'    => __( 'Choose first image for slider', 'music-band-lite' ),	    'section'  => 'codeinwp_slider_section',	    'settings' => 'slider_image1',		'priority' => 1	) ) );		/* second slide image */	$wp_customize->add_setting( 'slider_image2' );	$wp_customize->add_control( new WP_Customize_Image_Control( $wp_customize, 'slider_image2', array(	    'label'    => __( 'Choose second image for slider', 'music-band-lite' ),	    'section'  => 'codeinwp_slider_section',	    'settings' => 'slider_image2',		'priority' => 2	) ) );		/* third slide image */	$wp_customize->add_setting( 'slider_image3' );	$wp_customize->add_control( new WP_Customize_Image_Control( $wp_customize, 'slider_image3', array(	    'label'    => __( 'Choose third image for slider', 'music-band-lite' ),	    'section'  => 'codeinwp_slider_section',	    'settings' => 'slider_image3',		'priority' => 3	) ) );		/* first image big title */	$wp_customize->add_setting( 'slider_bigtitle1', 		array(			'default' => '','sanitize_callback' => 'music_band_lite_text_sanitization'		) 	);	$wp_customize->add_control('slider_bigtitle1', array(		'label'   => 'Enter a title to appear on the right side of the first slide',		'section' => 'codeinwp_slider_section',		'settings'   => 'slider_bigtitle1',		'priority' => 4	) );			/* second image big title */	$wp_customize->add_setting( 'slider_bigtitle2', 		array(			'default' => '','sanitize_callback' => 'music_band_lite_text_sanitization'		) 	);	$wp_customize->add_control('slider_bigtitle2', array(		'label'   => 'Enter a title to appear on the right side of the second slide',		'section' => 'codeinwp_slider_section',		'settings'   => 'slider_bigtitle2',		'priority' => 5	) );	/* third image big title */	$wp_customize->add_setting( 'slider_bigtitle3', 		array(			'default' => '','sanitize_callback' => 'music_band_lite_text_sanitization'		) 	);	$wp_customize->add_control('slider_bigtitle3', array(		'label'   => 'Enter a title to appear on the right side of the third slide',		'section' => 'codeinwp_slider_section',		'settings'   => 'slider_bigtitle3',		'priority' => 6	) );			/* first image title */	$wp_customize->add_setting( 'slider_title1', 		array(			'default' => '','sanitize_callback' => 'music_band_lite_text_sanitization'		) 	);	$wp_customize->add_control('slider_title1', array(		'label'   => 'Enter a title to appear on the left side of the first slide',		'section' => 'codeinwp_slider_section',		'settings'   => 'slider_title1',		'priority' => 7	) );			/* second image title */	$wp_customize->add_setting( 'slider_title2', 		array(			'default' => '','sanitize_callback' => 'music_band_lite_text_sanitization'		) 	);	$wp_customize->add_control('slider_title2', array(		'label'   => 'Enter a title to appear on the left side of the second slide',		'section' => 'codeinwp_slider_section',		'settings'   => 'slider_title2',		'priority' => 8	) );	/* third image title */	$wp_customize->add_setting( 'slider_title3', 		array(			'default' => '','sanitize_callback' => 'music_band_lite_text_sanitization'		) 	);	$wp_customize->add_control('slider_title3', array(		'label'   => 'Enter a title to appear on the left side of the third slide',		'section' => 'codeinwp_slider_section',		'settings'   => 'slider_title3',		'priority' => 9	) );		/* first image text*/	$wp_customize->add_setting( 'slider_text1', 		array(			'default' => '','sanitize_callback' => 'music_band_lite_text_sanitization'		) 	);	$wp_customize->add_control('slider_text1', array(		'label'   => 'Enter a text to appear on the left side of the first slide',		'section' => 'codeinwp_slider_section',		'settings'   => 'slider_text1',		'priority' => 7	) );			/* second image text */	$wp_customize->add_setting( 'slider_text2', 		array(			'default' => '','sanitize_callback' => 'music_band_lite_text_sanitization'		) 	);	$wp_customize->add_control('slider_text2', array(		'label'   => 'Enter a text to appear on the left side of the second slide',		'section' => 'codeinwp_slider_section',		'settings'   => 'slider_text2',		'priority' => 8	) );	/* third image text */	$wp_customize->add_setting( 'slider_text3', 		array(			'default' => '','sanitize_callback' => 'music_band_lite_text_sanitization'		) 	);	$wp_customize->add_control('slider_text3', array(		'label'   => 'Enter a text to appear on the left side of the third slide',		'section' => 'codeinwp_slider_section',		'settings'   => 'slider_text3',		'priority' => 9	) );			/********************************/	/*****  copyright ***************/	/********************************/		$wp_customize->add_section( 'codeinwp_copyright_section', array(		'title'          => __( 'Copyright','music-band-lite' ),		'priority'       => 42,	) );		$wp_customize->add_setting( 'codeinwp_copyright', array(		'default'        => '', 'sanitize_callback' => 'music_band_lite_text_sanitization'	) );	$wp_customize->add_control( 'codeinwp_copyright', array(		'label'   => 'Copyright',		'section' => 'codeinwp_copyright_section',		'settings'   => 'codeinwp_copyright'	) );		/***********************************************************/	/******** FOOTER section with boxes and social icons ******/	/***********************************************************/		/* on witch pages to appear */	$wp_customize->add_section( 'codeinwp_footer_section', array(		'title'          => __( 'Footer section with boxes and social icons','music-band-lite' ),		'priority'       => 43,	) );		/* background image */	$wp_customize->add_setting( 'footer_section_image' );	$wp_customize->add_control( new WP_Customize_Image_Control( $wp_customize, 'footer_section_image', array(	    'label'    => __( 'Choose an image for the footer section', 'music-band-lite' ),	    'section'  => 'codeinwp_footer_section',	    'settings' => 'footer_section_image',		'priority' => 2	) ) );		/* section1 - title */	$wp_customize->add_setting( 'footer_section_title1', 		array(			'default' => '', 'sanitize_callback' => 'music_band_lite_text_sanitization'		) 	);	$wp_customize->add_control('footer_section_title1', array(		'label'   => 'Enter a title for the first box',		'section' => 'codeinwp_footer_section',		'settings'   => 'footer_section_title1',		'priority' => 3	) );		/* section1 - text */	$wp_customize->add_setting( 'footer_section_text1', 		array(			'default' => '', 'sanitize_callback' => 'music_band_lite_text_sanitization'		) 	);	$wp_customize->add_control('footer_section_text1', array(		'label'   => 'Enter a text for the first box',		'section' => 'codeinwp_footer_section',		'settings'   => 'footer_section_text1',		'priority' => 4	) );		/* section2 - title */	$wp_customize->add_setting( 'footer_section_title2', 		array(			'default' => '', 'sanitize_callback' => 'music_band_lite_text_sanitization'		) 	);	$wp_customize->add_control('footer_section_title2', array(		'label'   => 'Enter a title for the second box',		'section' => 'codeinwp_footer_section',		'settings'   => 'footer_section_title2',		'priority' => 5	) );		/* section2 - text */	$wp_customize->add_setting( 'footer_section_text2', 		array(			'default' => '', 'sanitize_callback' => 'music_band_lite_text_sanitization'		) 	);	$wp_customize->add_control('footer_section_text2', array(		'label'   => 'Enter a text for the second box',		'section' => 'codeinwp_footer_section',		'settings'   => 'footer_section_text2',		'priority' => 6	) );		/* section3 - title */	$wp_customize->add_setting( 'footer_section_title3', 		array(			'default' => '', 'sanitize_callback' => 'music_band_lite_text_sanitization'		) 	);	$wp_customize->add_control('footer_section_title3', array(		'label'   => 'Enter a title for the third box',		'section' => 'codeinwp_footer_section',		'settings'   => 'footer_section_title3',		'priority' => 7	) );		/* section3 - text */	$wp_customize->add_setting( 'footer_section_text3', 		array(			'default' => '', 'sanitize_callback' => 'music_band_lite_text_sanitization'		) 	);	$wp_customize->add_control('footer_section_text3', array(		'label'   => 'Enter a text for the third box',		'section' => 'codeinwp_footer_section',		'settings'   => 'footer_section_text3',		'priority' => 8	) );		/* facebook link */	$wp_customize->add_setting( 'facebook', 		array(			'default' => '#','sanitize_callback' => 'esc_url_raw'		) 	);	$wp_customize->add_control('facebook', array(		'label'   => 'Enter the facebook link',		'section' => 'codeinwp_footer_section',		'settings'   => 'facebook',		'priority' => 9	) );		/* twitter link */	$wp_customize->add_setting( 'twitter', 		array(			'default' => '#','sanitize_callback' => 'esc_url_raw'		) 	);	$wp_customize->add_control('twitter', array(		'label'   => 'Enter the twitter link',		'section' => 'codeinwp_footer_section',		'settings'   => 'twitter',		'priority' => 10	) );		/* linkedin link */	$wp_customize->add_setting( 'linkedin', 		array(			'default' => '#','sanitize_callback' => 'esc_url_raw'		) 	);	$wp_customize->add_control('linkedin', array(		'label'   => 'Enter the linkedin link',		'section' => 'codeinwp_footer_section',		'settings'   => 'linkedin',		'priority' => 11	) );		/* END - FOOTER section with boxes and social icons */			/* END - FRONT PAGE SLIDER */}add_action( 'customize_register', 'codeinwp_customize_register' );/** * Binds JS handlers to make Theme Customizer preview reload changes asynchronously. */function cwp_customize_preview_js() {	wp_enqueue_script( 'cwp_customizer', get_template_directory_uri() . '/js/customizer.js', array( 'customize-preview' ), '20130508', true );}add_action( 'customize_preview_init', 'cwp_customize_preview_js' );function music_band_lite_text_sanitization( $input ) {    return wp_kses_post( force_balance_tags( $input ) );}if( class_exists( 'WP_Customize_Control' ) ):class Example_Customize_Textarea_Control extends WP_Customize_Control {    public $type = 'textarea';     public function render_content() {        ?>        <label>        <span class="customize-control-title"><?php echo esc_html( $this->label ); ?></span>        <textarea rows="5" style="width:100%;" <?php $this->link(); ?>><?php echo esc_textarea( $this->value() ); ?></textarea>        </label>        <?php    }}/** * Multiple select customize control class. */class cwp_Customize_Control_Multiple_Select extends WP_Customize_Control {    /**     * The type of customize control being rendered.     */    public $type = 'multiple-select';    /**     * Displays the multiple select on the customize screen.     */    public function render_content() {    if ( empty( $this->choices ) )        return;    ?>        <label>            <span class="customize-control-title"><?php echo esc_html( $this->label ); ?></span>            <select <?php $this->link(); ?> multiple="multiple" style="height: 100%;">                <?php                    foreach ( $this->choices as $value => $label ) {                        $selected = ( in_array( $value, $this->value() ) ) ? selected( 1, 1, false ) : '';                        echo '<option value="' . esc_attr( $value ) . '"' . $selected . '>' . $label . '</option>';                    }                ?>            </select>        </label>    <?php }}endif;