<?php/* * The Template for displaying all single posts. * * @package music-band-lite */get_header(); ?><?php while ( have_posts() ) : the_post(); ?>		<!--Content Start-->		<section id="no-subheader"></section>		<div id="wraper">			<section id="content">				<div <?php post_class( 'post_inside' ); ?>>					<div class="topdetails">						<h2><a href=""><?php the_title(); ?></a></h2>						<div class="details">							<?php								/* date */								echo get_the_date( 'F j, Y' ).' &#8226; ';								/* author */
								echo '<a href="'.get_author_posts_url( get_the_author_meta( 'ID' )).'">'.get_the_author().'</a> &#8226; ';								/* comments */
								comments_number( __( 'No Comments', 'music-band-lite' ), __( 'one Comment', 'music-band-lite' ), 									'% '.__( 'Comments', 'music-band-lite' ) );								echo ' &#8226; ';								/* categories */	
								$cat = get_the_category();								if ( ! empty( $cat )) :										foreach( $cat as $cat_item ):											echo '<a href="'.get_category_link( $cat_item->cat_ID ).'">' . $cat_item  ->cat_name.'</a> &#8226; ';										endforeach;								endif;								if ( has_tag()):
									the_tags();									echo ' &#8226; ';								endif;							?>						</div>					</div><!--/topdetails-->					<div class="clearfix"></div>					<?php 						if ( get_theme_mod( 'fi_single' )):							$fi_single = get_theme_mod( 'fi_single' );						endif;							if ( ( isset( $fi_single ) && $fi_single == 'show' ) || ( ! isset( $fi_single )) ){							if ( has_post_thumbnail()) {								echo '<figure>'.get_the_post_thumbnail().'</figure>';							}						}											?>					<article>						<?php the_content(); ?>						<?php wp_link_pages(); ?>					</article>										<?php comments_template(); ?>				</div><!--/post-->			</section><!--/content-->			<aside id="sidebar">				<?php get_sidebar(); ?>			</aside><!--/sidebar-->			<div class="clearfix"></div>		</div><!--/wraper-->	<?php	endwhile;	get_footer(); ?>